INSERT INTO `zb_gyzb_fzlc` (`id`, `title`, `paixu`, `content`, `addtime`, `adduser`, `moditime`, `modiuser`, `status_del`) VALUES
(1, '2017', 17, '&nbsp; &nbsp; 2017年为推进&ldquo;一带一路&rdquo;教育共同繁荣，中博教育与在&ldquo;一带一路&rdquo;沿线国家中的首个合作伙伴蒙古国财经大学达成全面战略合作，搭建国际教育交流平台，共同推动国际财经教育的全面发展和国际财经人才培养。<br />\r\n', 1502782408, '1', 1503984231, '3', 0),
(2, '2016', 16, '&nbsp; &nbsp; 截止2016年12月获得ACCA协会十项黄金级认证，成为中国十项黄金级认证的培训机构，并自主研发ACCA Helper，和全球著名出版集团Wiley就联合开发CFA和FRM教学教研工作达成战略合作。<br />\r\n', 1502782422, '1', 1503984240, '3', 0),
(3, '2004', 4, '&nbsp; &nbsp; 2004年公司成立，并首次开发&ldquo;适合中国人自己的ACCA讲义&rdquo;。', 1503115171, '1', 1503984325, '3', 0),
(4, '2005', 5, '&nbsp; &nbsp; 2005年首创ACCA在线网络学习课程，ACCA教育开始遍布全国。<br />\r\n', 1503115236, '1', 1503984318, '3', 0),
(5, '2006', 6, '&nbsp; &nbsp; 2006年成为对外经济贸易大学ACCA方向班课程合作伙伴，将优质的ACCA课程带入大学正式课堂，迄今中博诚通已与中国149所大学形成战略合作伙伴关系。<br />\r\n', 1503115259, '1', 1503984312, '3', 0),
(6, '2007', 7, '&nbsp; &nbsp; 2007年成为众多世界500强的企业内训课程合作伙伴。', 1503115311, '1', 1503984305, '3', 0),
(7, '2008', 8, '&nbsp; &nbsp; 2008年被ACCA英国总部授予黄金级培训机构，成为全国第一批获得该荣誉的社会化培训机构。<br />\r\n', 1503115335, '1', 1503984298, '3', 0),
(8, '2009', 9, '&nbsp; &nbsp; 2009年与爱尔兰格里菲斯学院签署合作协议，将ACCA学习与海外留学融为一体，迄今与英美法加多所优秀海外大学形成战略合作。<br />\r\n', 1503115366, '1', 1503984291, '3', 0),
(9, '2010', 10, '&nbsp; &nbsp; 2010年中博诚通累计培训学员超过10000人次，迄今已有超过65,000人次的中国人学习到中博诚通优质课程。<br />\r\n', 1503115388, '1', 1503984284, '3', 0),
(10, '2011', 11, '&nbsp; &nbsp; 2011年被ACCA英国总部授予ACCA机考中心资格，迄今中博诚通拥有39个机考中心，成为中国规模最大的机考中心。<br />\r\n&nbsp; &nbsp;同年被美国管理会计师协会IMA授权为CMA培训机构，首次将CMA高端管理会计课程引入大学本科课堂。<br />\r\n', 1503115436, '1', 1503984278, '3', 0),
(11, '2012', 12, '&nbsp; &nbsp; 2012年中博诚通成熟分部ACCA考试通过率首次由ACCA英国总部官方统计并全面超越全球通过率。<br />\r\n&nbsp; &nbsp;被ICAEW英格兰及威尔士特许会计师协会总部授予授权ACA课程认证教学机构；同年，被CIMA皇家特许管理会计师公会认定为授权培训机构。<br />\r\n', 1503115468, '1', 1503984269, '3', 0),
(12, '2013', 13, '&nbsp; &nbsp; 2013年中博诚通在全国新增了9所分部，迄今中博诚通在全国共有49所直营分部149个教学点。', 1503115494, '1', 1503984260, '3', 0),
(13, '2014', 14, '&nbsp; &nbsp; 2014年中博诚通教学研发中心研究开发的ACCA习题集由长江出版社出版，是全球第一部完全由中国人编写的拥有完全自主知识产权的ACCA备考习题集。<br />\r\n', 1503116066, '1', 1503984253, '3', 0),
(14, '2015', 15, '&nbsp; &nbsp; 2015年中博教育获得ACCA协会6项黄金级认证，成为中国一家获得6项黄金级认证的培训机构。<br />\r\n', 1503116098, '1', 1503984246, '3', 0),
(15, '2018', 22, '啊啊啊', 1503556748, '4', 1503556748, '4', 1),
(16, '哈哈哈', 33, '<img alt=\"angel\" height=\"20\" src=\"http://zb.php.xmfeel.com/statics/js/ckeditor/plugins/smiley/images/angel_smile.gif\" title=\"angel\" width=\"20\" />', 1503556855, '4', 1503556855, '4', 1);
